mapper.insert:
insert into blog_post (
	post_no
	, post_title
	, post_contents
	, post_uploaddate
	, user_id


	, post_book_title 



	, post_originalfile
	, post_savedfile

	)
values(
	post_no_seq.nextval
	, #{post_title}
	, #{post_contents}
	, sysdate
	, #{user_id}


	 , #{post_book_title} 



	, #{post_originalfile}
	, #{post_savedfile}

	)

mapper.select:
select * from blog_post where user_id = #{user_id}
and post_title like '%' || #{searchText} || '%'
order by post_no desc

mapper.delete:
delete
from
	blog_post
where
	post_no = #{post_no}
	and user_id = #{user_id}

mapper.update:
		update
			blog_post
		set
  			post_title = #{post_title}
  			, post_contents = #{post_contents}
  			, post_uploaddate = sysdate

  			, post_originalfile = #{post_originalfile}
  			, post_savedfile = #{post_savedfile}



  			 , post_book_title = #{post_book_title} 


  		where
  			post_no = #{post_no}
			and user_id = #{user_id}

mapper.insert:
 insert into post_comment (
