	, #{post_no}
	, #{post_comment_writter}
	, #{post_comment_content}
	)

mapper.delete:
delete
from
	post_comment
where
	post_comment_no = #{post_comment_no}
	and post_comment_writter = #{post_comment_writter}

mapper.update:
update
	post_comment
set
