mapper.insert:
insert into message(
	message_no
	,	message_sent_id
	,	message_recv_id
	,	message_contents
	, 	message_date
	)
values(
	message_seq.nextval
	,	#{message_sent_id}
	,	#{message_recv_id}
	,	#{message_contents}
	,	sysdate
	)

mapper.select:
select message_no , substr(message_contents,0, 9) as message_title , message_contents , message_date , message_sent_id , message_recv_id , message_hits from message where message_recv_id = #{login_id}
and message_contents like '%' || #{searchText} || '%'
order by message_no desc

select message_no , substr(message_contents,0, 9) as message_title , message_contents , message_date , message_sent_id , message_recv_id from message where message_sent_id = #{login_id}
and message_contents like '%' || #{searchText} || '%'
order by message_no desc
