mapper.insert:
insert into book_review (
	book_review_no
	, book_no
	, book_review_writer
	, book_review_short
	, book_review_date
	, book_review_star
	, book_isbn
)
values (
	book_review_seq.nextval
	, #{book_no}
	, #{book_review_writer}
	, #{book_review_short}
	, #{book_review_date}
	, #{book_review_star}
	, #{book_isbn}
)

mapper.delete:
delete
from
	book_review
where
	book_review_no = #{book_review_no}
	and book_review_writer = #{book_review_writer}

mapper.update:
update
	book_review
set
	book_review_short = #{book_review_short}
where
	book_review_no = #{book_review_no}
	and book_review_writer = #{book_review_writer}
